import React, { useState, useEffect } from "react";
import Slider from "react-slick";
import "./imageComponentStyle.css";

function ImageComponent() {
  const [error, setError] = useState(null);
  const [items, setItems] = useState([]);
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
  };

  useEffect(() => {
    fetch("https://krds-assignment.github.io/aoc/api-assets/data.json")
      .then((res) => res.json())
      .then(
        (result) => {
          setItems(result.features);
        },
        (error) => {
          setError(error);
        }
      );
  }, []);

  return (
    // <ul>
    //   {items &&
    //     items.map((item) => (
    //       <li key={item.title}>
    //         <img src={item.logo} />
    //         <h4>{item.title}</h4>
    //         <h4>{item.desc}</h4>
    //         <img src={item.image} />
    //       </li>
    //     ))}
    // </ul>

    <ul>
      <Slider {...settings}>
        {items &&
          items.map((item) => (
            <li key={item.title}>
              <img src={item.logo} />
              <h4>{item.title}</h4>
              <h4>{item.desc}</h4>
              <img src={item.image} />
            </li>
          ))}
      </Slider>
    </ul>

    // <Slider {...settings}>
    //   <div>
    //     <h3>1</h3>
    //   </div>
    //   <div>
    //     <h3>2</h3>
    //   </div>
    //   <div>
    //     <h3>3</h3>
    //   </div>
    //   <div>
    //     <h3>4</h3>
    //   </div>
    //   <div>
    //     <h3>5</h3>
    //   </div>
    //   <div>
    //     <h3>6</h3>
    //   </div>
    // </Slider>
  );
}

export default ImageComponent;
